INSERT INTO `categories` (`id`, `name`, `Column 3`, `description`, `slug`, `status`, `created_at`, `updated_at`) VALUES
	(1, 'PC Games', '', 'PC games are diverse digital experiences played on personal computers. They span genres, support modding, online multiplayer, high-end graphics, and customization. PC gaming offers access to a vast library of titles and fosters a thriving esports scene.', 'pc-games', 1, '2023-11-06 22:18:57', '2023-12-16 22:49:00'),
	(2, 'Mobile Games', '', 'Mobile games are digital diversions for smartphones and tablets. They feature diverse genres, accessible touch controls, and often adopt free-to-play models with in-app purchases. These games cater to casual gamers and provide on-the-go entertainment.', 'mobile-games', 1, '2023-11-06 22:19:54', '2023-11-06 22:20:32'),
	(3, 'Gift Cards', '', 'Vouchers/gift cards are prepaid cards or digital codes with a set monetary value, redeemable at specific stores or for various services. They serve as versatile gifts, offering recipients the freedom to select their preferred items or experiences.', 'vouchers-gift-cards', 1, '2023-11-06 22:23:44', '2023-11-12 02:07:19'),
	(4, 'Subscriptions', '', 'Entertainment subscriptions offer unlimited on-demand content (e.g., movies, TV, music, games) for a recurring fee, delivering convenience and variety to subscribers across multiple devices.', 'subscription-entertainment', 1, '2023-11-06 22:27:05', '2023-11-12 02:06:53');
