INSERT INTO `products` (`id`, `category_id`, `name`, `slug`, `icon`, `cover_image`, `thumb_image`, `description`, `how_to_order`, `status`, `seo_title`, `seo_description`, `ign_required`, `region_required`, `pass_required`, `tag_required`, `ign_type`, `ign_example`, `region_type`, `password_type`, `tag_type`, `created_at`, `updated_at`) VALUES
	(1, 1, 'Valorant (Malaysia)', 'valorant-malaysia', 'uploads/products/icons/media_654c144848066.png', 'uploads/products/covers/media_6549ad3ca4818.jpg', 'uploads/products/thumbnails/media_6549af7a8c715.jpg', '<p><div style=""><span style="font-family: Arial;">Valorant  is a F2P (free to play) 5v5 shooter game where two teams plant or defuse the Spike in a one-life-per-round, first to 13 series. Annihilating the opposing team will win you the round as well. More than just guns & bullets, VALORANT lets you choose an Agent armed with unique abilities that enhances your gunplay experience.</span></div><span id="docs-internal-guid-e992e51f-7fff-9785-e472-9745db1a133e" style="font-family: Arial;"><div style="">Riot Games presents a new FPS where you can explore your skills. Your biggest weapon in this game is the creativity to win in the tactical shooting mode in teams of 5.</div></span></p>', '<h2 style="margin-top: 0pt; margin-bottom: 0pt; line-height: 1.38;"></h2><p></p><div style="">1. Select quantity</div><div style="">2. Enter your Valorant Riot ID(IGN + #Tagline)</div><div style="">3. Check out and select your payment method</div><div style="">4. Once payment is made, the VP you purchased will be credited to your Valorant Account shortly</div><p></p><p style="list-style-type: decimal; font-size: 10.5pt; font-family: Roboto, sans-serif; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-variant-position: normal; vertical-align: baseline; white-space: pre;"></p><ul><ul></ul></ul><p></p><p style="list-style-type: decimal; font-size: 10.5pt; font-family: Roboto, sans-serif; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-variant-position: normal; vertical-align: baseline; white-space: pre;"></p><ul><ul></ul></ul><p></p><p style="list-style-type: decimal; font-size: 10.5pt; font-family: Roboto, sans-serif; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-variant-position: normal; vertical-align: baseline; white-space: pre;"></p><ul><ul></ul></ul><p></p>', 1, 'Valorant Malaysia', 'Valorant Malaysia VP for the cheapest price', 1, 0, 0, 0, 'Riot ID', 'example #0000', 'Region', 'Password', NULL, '2023-11-07 03:21:32', '2023-12-16 22:49:25'),
